from logging import getLogger, INFO
import os
from cloghandler import ConcurrentRotatingFileHandler

log = getLogger()
logfile = os.path.abspath("mylogfile.log")
# Rotate log after reaching 512K, keep 20 old copies.
rotateHandler = ConcurrentRotatingFileHandler(logfile, "a", 512*1024, 20)
log.addHandler(rotateHandler)
log.setLevel(INFO)


